Glossary = [{ t: "وحدة القياس", d: "<br>هي صفة فيزيائية تصف ظاهرة طبيعية يمكن قياسها أو حسابها.<br>" }, { t: "النظام الإنجليزي", d: "<br>وحدة قياس معيارية تستخدم لقياس كمية فيزيائية.	<br>" }, { t: "النظام الجاوسي", d: "<br>ويرمز له بالرمز ( F P S ). اعتمد في هذا النظام الوحدات الأساسية  الآتية: القدم ( ft )  لقياس الطول، والباوند ( lb )  لقياس الكتلة، والثانية  (s) لقياس الزمن. <br>" }, { t: "النظام (الدولي)", d: "<br>ويرمز له بالرمز ( C G S ). اعتمد في هذا النظام الوحدات الأساسية الآتية:  السنتيمتر (cm) لقياس الطول، والجرا م (g) لقياس الكتلة، والثانية  ( s ) لقياس الزمن<br>" }, { t: "الكميات الفيزيائية الأساسية", d: "<br>ويرمز له بالرمز (SI ) . اعتمد في هذا النظام الوحدات الأساسية الآتية: المتر (m) لقياس الطول، وال كيلوجرام (Kg) لقياس الكتلة، والثانية  ( s ) لقياس الزمن، والأمبير (I) لقياس شدة التيار الكهربائي، والكلفن (K) لقياس درجة الحرارة، والقنديل أو شمعة (cd) لقياس شدة الإضاءة، والمول (ml) لقياس كمية المادة.<br>" }, { t: "الكميات الفيزيائية المشتقة", d: "<br>هي الكميات التي لا يمكن اشتقاقها من كميات أبسط منها وهي معروفة بذاتها<br>" }, { t: "القدمة ذات الورنية", d: "<br>هي الكميات التي تستنبط من كميات أخرى بواسطة القوانين الفيزيائية .<br>" }, { t: "الميكرومتر", d: "<br>تستخدم القدمة ذات الورنية لقياس الأبعاد الخارجية والداخلية للأجسام، وكذلك أعماق الثقوب. وهي تقيس أبعادًا بدقة تصل إلى  0.1 mm أو 0.05 mm أو 0.02 mm<br>" }, { t: "الكتلة", d: "<br> يستخدم الميكرومتر لقياس سمك الأبعاد الخارجية للأجسام . مثل: الشرائح الرقيقة  ، و أقطار الأسلاك الرفيعة بدقة عالية تبلغ 0.01 mm<br>" }, { t: "الكثافة", d: "<br>هي مقدار ما يحتويه الجسم من مادة<br>" }, { t: "الكثافة النسبية ( الوزن النوعي )", d: "<br>هي كتلة وحدة الحجم من المادة<br>" }, { t: "القوة", d: "<br>هي النسبة بين كثافة المادة  و كثافة الماء  عند درجة الحرارة 4 ̊ C ، و هي درجة الحرارة التي تكون حينها كثافة الماء 1000 kg / cm3  . <br>" }, { t: "الوزن", d: "<br>هي ذلك المؤثر الذي إذا أثر على جسم ما، فإنه يسبب تغيراً في شكل الجسم أو موضعه أو في اتجاهه أو كمية حركته<br>" }, { t: "المرونة", d: "<br>هو مقدار قوة جذب الأرض للجسم<br>" }, { t: "حد المرونة", d: "<br>هي  ميل المادة للعودة إلى حالتها الأصلية بعد زوال القوى المؤثرة عليها .<br>" }, { t: "قانون هوك", d: "<br>هي أكبر قوة يمكن أن نؤثر بها على المادة دون أن تفقد مرونتها<br>" }, { t: "الطاقة الداخلية لمادة ", d: "<br>تحت حد المرونة فإن الاستطالة تتناسب طردياً مع قوة الشد المؤثرة . عليها<br>" }, { t: "درجة الحرارة", d: "<br>هي مجموع جميع أنواع الطاقة التى تمتلكها الذرات أو الجزيئات المكونة للمادة<br>" }, { t: "الترمومترات", d: "<br>هي مقياس للطاقة الداخلية للمادة . وهي الخاصية التي تحدد اتجاه انتقال الحرارة بين الأجسام حين تلامسها<br>" }, { t: "مقياس سلزيوس", d: "<br>هي الأجهزة المستخدمة لقياس درجة الحرارة ، وهي تعتمد في عملها على خاصية فيزيائية تتغير بتغير درجة الحرارة تغيراً واضحاً ومعلوماً كالتمدد الحراري للمادة أو التغير في المقاومة الكهربائية.<br>" }, { t: "مقياس فهرنهيت", d: "<br>والمشهور بالمئوي ( سنتيجراد ) ، وفيه تعد نقطة تجمد الماء النقي عند الدرجة 0°c ونقطة الغليان عند 100°c  . <br>" }, { t: "مقياس كلفن أو المقياس المطلق", d: "<br>وفيه تعد نقطة تجمد الماء النقي عند  و درجة غليانه<br>" }, { t: "كمية الحرارة", d: "<br>يستخدم في المجال العلمي، ووحدة درجة الحرارة في النظام الدولي SI هي كلفن ، وفي هذا المقياس فإن نقطتي تجمد وغليان الماء هما : 273 K  و 373 K  على الترتيب . <br><br>" }, { t: "الحرارة النوعية لمادة", d: "<br>هي الطاقة الحرارية التي تنتقل من جسم ساخن إلى جسم بارد نتيجة لاختلاف درجتي الحرارة بين الجسمين<br>" }, { t: "معامل التمدد الطولي", d: "<br>هي كمية الحرارة اللازمة لرفع درجة حرارة كيلو جرام من المادة درجة واحدة<br>" }, { t: "الإزاحة", d: "<br>: هو مقدار الزيادة في وحدة الأطوال من المادة حين رفع درجة حرارتها درجة واحدة <br>" }, { t: "السرعة المتوسطة", d: "<br>هي المتجه الواصل بين نقطتين بدايته عند إحدى هاتين النقطتين و نهايته عند النقطة الأخرى ، و مقداره طول المسافة المستقيمة بين تلك النقطتين<br>" }, { t: "التسارع المتوسط", d: "<br> مقدار التغير في الإزاحة  مقسومًا على الزمن<br>" }]
intro = true;
